    def speechLowFile(self, filename, language):
        # open the file
        with sr.AudioFile(filename) as source:
            # listen for the data (load audio to memory)
            audio_data = r.record(source)
            # recognize (convert from speech to text)
            text = r.recognize_google(audio_data, language = language)
            print(text)

    def convert(self):
        langue = self.comboBox.currentText()
        #print(self.comboBox.currentText())
        self.speechLowFile(self.label.text(),langue)

    def get_file(self):
        file , check = QFileDialog.getOpenFileName(None, "QFileDialog.getOpenFileName()","", "All Files (*);;Mp3 Files (*.mp3);;Mp4 Files (*.mp4);;Wav Files (*.wav)")
        if check:
            #speechLowFile(file)
            self.label.setText(file)
            return file


